/* global fetch */

import React, { Component } from 'react'
import Slider from 'react-slick'

class Articles extends Component {
  constructor (props) {
    super(props)
    this.state = {
      'showLoading': true,
      'showError': false
    }
    const settings = {
      dots: true,
      infinite: true,
      slidesToShow: 4,
      slidesToScroll: 1,
      autoplay: true,
      autoplaySpeed: 2500,
      rtl: false
    }
    this.elements = []
  }

  componentDidMount () {
    fetch(`http://46.188.118.134:8082/types?name_en=${this.props.type}`)
      .then(response => response.json())
      .then(response => {
        response
          .pop()
          .articles
          .sort((a, b) => a.createdAt < b.createdAt ? 1 : -1)
          .forEach(article => {
            if (article.is_active === true) {
              this.elements.push(
                <Slider {...settings}>
                <div className='column is-3'>
                  <div className='card'>
                    <div className='card-image'>
                      <figure className='image is-4by3'>
                        <img src={`http://46.188.118.134:8082/${article.poster.url}`} />
                      </figure>
                    </div>
                    <div className='card-content'>
                      {article.title_ru}
                    </div>
                  </div>
                </div>
                </Slider>
              )
            }
          })
        this.setState({
          'showLoading': false,
          'showError': false
        })
      })
      .catch(error => {
        console.error(error)
        this.setState({
          'showLoading': false,
          'showError': true
        })
      })
  }

  render () {
    if (this.state.showLoading === true) {
      return (
        <p>Loading</p>
      )
    }
    if (this.state.showError === true) {
      return (
        <p>Error</p>
      )
    }
    return (
      <div className='columns'>
        {this.elements}
      </div>
    )
  }
}

export default Articles
