import React, { Component } from 'react'
import Slider from 'react-slick'
import { MyContext } from '../'

export default class Rtl extends Component {
  render () {
    const settings = {
      dots: true,
      infinite: true,
      slidesToShow: 3,
      slidesToScroll: 1,
      autoplay: true,
      autoplaySpeed: 2500,
      rtl: false
    }
    return (

      <MyContext.Consumer>
        { value =>
          <span>
            {value.map(item => (item.title_ru)
            )};
            </span>
        }
      </MyContext.Consumer>

    )
  }
}
